        + '@keyframes pulse { 0%, to { opacity: 1 } 50% { opacity: .5 }}'
        + '.balloon-tip { content: "" ; position: relative ; border: 7px solid transparent ;'
            + ( isChromium ? 'top: 0.16em ; right: 5.1rem;' : 'top: 0.25em ; right: 10.38rem ;' )
